const ProfessionalTools: React.FC<ProfessionalToolsProps> = ({
  tools,
  selectedCategory,
  selectedSubcategory,
  professionalCategories,
  searchTerm,
}) => {
  // Find the selected category and subcategory objects
  const category = professionalCategories.find(cat => cat.id === selectedCategory);
  const subcategory = category?.subcategories.find(sub => sub.id === selectedSubcategory);
  
  console.log('Selected Category:', selectedCategory);
  console.log('Selected Subcategory:', selectedSubcategory);
  
  // Use the internal professionalTools array for filtering
  const toolsToFilter = professionalTools;
  
  // Filter professional tools based on selected category and subcategory
  const filteredTools = toolsToFilter.filter(tool => {
    // For category mapping
    const categoryMapping: Record<string, string> = {
      'business': 'AI for Business',
      'content': 'AI for Content Creation',
      'development': 'AI for Development',
      'students': 'AI for Students',
      'creatives': 'AI for Creatives',
      'healthcare': 'AI for Healthcare'
    };
    
    // Get the full category name from the mapping
    const fullCategoryName = categoryMapping[selectedCategory] || '';
    
    // Match by category
    if (tool.category !== fullCategoryName) {
      return false;
    }
    
    // Filter by search term if provided
    if (searchTerm && searchTerm.trim() !== '') {
      const searchLower = searchTerm.toLowerCase();
      const nameMatch = tool.name.toLowerCase().includes(searchLower);
      const descMatch = tool.description.toLowerCase().includes(searchLower);
      
      if (!nameMatch && !descMatch) {
        return false;
      }
    }
    
    // If subcategory is selected, filter by subcategory too
    if (selectedSubcategory && subcategory) {
      // Add more flexible matching for subcategories
      const subcategoryMatches = 
        // Direct match
        tool.subcategory === subcategory.name ||
        // Case insensitive match
        tool.subcategory?.toLowerCase() === subcategory.name.toLowerCase() ||
        // Check if the subcategory ID is in the tool's tags
        tool.tags?.some(tag => tag.toLowerCase() === selectedSubcategory.toLowerCase()) ||
        // Check if the subcategory name is in the tool's tags
        tool.tags?.some(tag => tag.toLowerCase() === subcategory.name.toLowerCase());
      
      return subcategoryMatches;
    }
    
    // If no subcategory selected, return all tools in the category
    return true;
  });

  // Deduplicate tools by ID to prevent the same tool from appearing multiple times
  const uniqueFilteredTools = Array.from(
    new Map(filteredTools.map(tool => [tool.id, tool])).values()
  );

  if (!selectedCategory || !category) {
    return (
      <div className="p-6 bg-one-darker rounded-lg border border-one-border">
        <p className="text-white opacity-70">Please select a professional category</p>
      </div>
    );
  }

  return (
    <div className="w-full">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-white">{subcategory ? subcategory.name : category.name}</h2>
        <p className="text-gray-300 mt-2">
          {subcategory ? subcategory.description : category.description}
        </p>
      </div>

      {uniqueFilteredTools.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {uniqueFilteredTools.map((tool) => (
            <div key={tool.id} className="bg-[#1F1F2E] rounded-lg p-5 h-full flex flex-col hover:border-gray-700 transition-all duration-200">
              {/* Top section with icon */}
              <div className="mb-3">
                <div className="h-10 w-10 rounded bg-[#2D2D3E] flex items-center justify-center overflow-hidden">
                  {tool.icon ? (
                    <img 
                      src={tool.icon} 
                      alt={tool.name} 
                      className="h-full w-full object-cover"
                    />
                  ) : (
                    <span className="text-xs text-white font-medium">
                      {tool.name.substring(0, 2).toUpperCase()}
                </span>
                  )}
                </div>
              </div>
              
              {/* Name and category badge */}
              <div className="flex justify-between items-center mb-2">
                <h3 className="font-medium text-white text-lg">{tool.name}</h3>
                <div className="px-3 py-1 bg-[#2D2D3E] text-[#8969FF] rounded-full text-xs">
                  {tool.subcategory || tool.category.replace('AI for ', '')}
                </div>
              </div>
              
              {/* Description */}
              <p className="text-sm text-gray-300 mb-auto">{tool.description}</p>
              
              {/* Rating and pricing */}
              <div className="flex items-center justify-between text-xs text-gray-300 mt-4 mb-4">
                <div className="flex items-center">
                  <svg 
                    xmlns="http://www.w3.org/2000/svg" 
                    width="14" 
                    height="14" 
                    viewBox="0 0 24 24" 
                    fill="none" 
                    stroke="currentColor" 
                    strokeWidth="2" 
                    strokeLinecap="round" 
                    strokeLinejoin="round"
                    className="text-yellow-400 mr-1"
                  >
                    <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
                  </svg>
                  <span>{tool.rating.toFixed(1)}</span>
                </div>
                <div>
                  <span>{tool.pricing}</span>
                </div>
              </div>
              
              {/* Try Tool button */}
              <a 
                href={tool.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-[#8969FF] hover:text-[#9979FF] text-sm font-medium flex items-center"
              >
                Try Tool 
                <svg 
                  xmlns="http://www.w3.org/2000/svg" 
                  width="14" 
                  height="14" 
                  viewBox="0 0 24 24" 
                  fill="none" 
                  stroke="currentColor" 
                  strokeWidth="2" 
                  strokeLinecap="round" 
                  strokeLinejoin="round"
                  className="ml-1"
                >
                  <line x1="5" y1="12" x2="19" y2="12"></line>
                  <polyline points="12 5 19 12 12 19"></polyline>
                </svg>
              </a>
            </div>
          ))}
        </div>
      ) : (
        <div className="p-6 bg-one-darker rounded-lg border border-one-border">
          <p className="text-gray-300">No tools found for this category. We're adding new tools regularly!</p>
        </div>
      )}
    </div>
  );
};

export default ProfessionalTools; 